// snackbar.min.js
(function () {
    var Snackbar = function () {
        var snackbarContainer = document.createElement('div');
        snackbarContainer.className = 'snackbar-container';
        document.body.appendChild(snackbarContainer);

        var showSnackbar = function (message, options) {
            var snackbar = document.createElement('div');
            snackbar.className = 'snackbar';
            snackbar.textContent = message;

            if (options && options.dismissible) {
                var dismissButton = document.createElement('span');
                dismissButton.textContent = 'âœ–';
                dismissButton.className = 'snackbar-dismiss';
                dismissButton.addEventListener('click', function () {
                    hideSnackbar(snackbar);
                });
                snackbar.appendChild(dismissButton);
            }

            snackbarContainer.appendChild(snackbar);

            setTimeout(function () {
                snackbar.classList.add('show');
            }, 10);

            if (!options || !options.timeout || options.timeout > 0) {
                setTimeout(function () {
                    hideSnackbar(snackbar);
                }, options && options.timeout ? options.timeout : 5000);
            }
        };

        var hideSnackbar = function (snackbar) {
            snackbar.classList.remove('show');
            setTimeout(function () {
                snackbarContainer.removeChild(snackbar);
            }, 300);
        };

        return {
            show: showSnackbar
        };
    };

    if (typeof window.Snackbar === 'undefined') {
        window.Snackbar = new Snackbar();
    }
})();
